INSERT INTO PRODUCTO (id, nombre, imagen, marca, categoria, precio, clasificacion, descripcion) VALUES (NEXTVAL('hibernate_sequence'),'Hunting Sword', 'https://s3.amazonaws.com/koa-media-library/wp-media-folder-kult-of-athena//www/wp-content/uploads/2020/11/CS88CLQ_L-700x801.jpg', 'ColdSteel', 'Espada', 235.12, 'BattleReady', 'This Hunting Sword (or Hanger) from Cold Steel has a sharpened blade forged from 1055 high carbon steel.');
INSERT INTO PRODUCTO (id, nombre, imagen, marca, categoria, precio, clasificacion, descripcion) VALUES (NEXTVAL('hibernate_sequence'),'Castillon Arming Sword', 'https://s3.amazonaws.com/koa-media-library/wp-media-folder-kult-of-athena/wp-content/uploads/2022/03/AH7032_6.jpg', 'Deepeeka', 'Espada', 186.95, 'BattleReady', 'The Castillon Arming Sword has a blade which is forged from C60 high carbon steel; its notable profile taper begins with a wide and durable base for the blade which tapers to acute and deadly thrusting tip');
INSERT INTO PRODUCTO (id, nombre, imagen, marca, categoria, precio, clasificacion, descripcion) VALUES (NEXTVAL('hibernate_sequence'),'English Bill', 'https://s3.amazonaws.com/koa-media-library/wp-media-folder-kult-of-athena//www/wp-content/uploads/2020/11/AA030_1_L.jpg', 'Arms&Armor', 'Arma de Asta', 425, 'BattleReady', 'With it’s origins as an agricultural tool, the Bill became a favored weapon of the English foot soldier from the 14th to the 17th century, seeing service from the Scottish border to wars in France.');
INSERT INTO PRODUCTO (id, nombre, imagen, marca, categoria, precio, clasificacion, descripcion) VALUES (NEXTVAL('hibernate_sequence'), 'Friedrich IV Spear', 'https://s3.amazonaws.com/koa-media-library/wp-media-folder-kult-of-athena//www/wp-content/uploads/2020/11/AA112_3_L.jpg', 'Arms&Armor', 'Arma de Asta', 245.56, 'BattleReady', 'This style of spear is often called a lugged spear. It is developed from the Carolingian period fighting spear. The lugs were used in the fencing style of the time, to help control the opponent’s weapon.');
INSERT INTO PRODUCTO (id, nombre, imagen, marca, categoria, precio, clasificacion, descripcion) VALUES (NEXTVAL('hibernate_sequence'),'War Hammer', 'https://s3.amazonaws.com/koa-media-library/wp-media-folder-kult-of-athena//www/wp-content/uploads/2020/11/AA005_2_L.jpg', 'Arms&Armor', 'Arma Pesada', 368.34, 'BattleReady', 'This 15th Century War Hammer is a replica of a French weapon crafted around 1450; It now resides in the London Wallace Collection.');
INSERT INTO PRODUCTO (id, nombre, imagen, marca, categoria, precio, clasificacion, descripcion) VALUES (NEXTVAL('hibernate_sequence'),'German Flail', 'https://s3.amazonaws.com/koa-media-library/wp-media-folder-kult-of-athena//www/wp-content/uploads/2020/11/AA144_3_L.jpg', 'Arms&Armor', 'Arma Pesada', 406.11, 'BattleReady', 'The Arms & Armor German Flail is based on a 15th century original now housed in the Metropolitan Museum of Art, New York. This single hand flail was used both on foot or on horseback.');
